mapper.sql:
from t_device t left join t_area a on t.area_id = a.id where 1=1
and ( t.point_number like CONCAT('%',#{searchName,jdbcType=VARCHAR},'%') OR t.point_name like CONCAT('%',#{searchName,jdbcType=VARCHAR},'%') OR t.address like CONCAT('%',#{searchName,jdbcType=VARCHAR},'%') )
and t.POINT_NUMBER = #{pointNumber,jdbcType=VARCHAR}
and t.id != #{id,jdbcType=VARCHAR}

mapper.select:
	 select ,a.name as AREA_NAME  

  order by t.id desc

      limit #{pageStart}, #{pageSize}
